import React from 'react'
import { motion } from "framer-motion";

const Project1 = () => {

    const projectVariant = {
        hidden: { opacity: 0, scale: 0.8 },
        visible: { opacity: 1, scale: 1 },
      };

    const overlayStyles = `absolute h-full w-full opacity-0 hover:opacity-90 transition duration-500
    bg-grey z-30 flex flex-col justify-center items-center text-center p-16 text-deep-blue`;

    return (
        <motion.div variants={projectVariant} className="relative">
            <div className={overlayStyles}>
                <p className="text-2xl font-playfair">E-Commerce</p>
                <p className="mt-7">
                    Full Responsive app emulating the process of shopping in an E-Commerce where you can Sign up, Login, add to cart, checkout and view past purchases. Feel free to use the test data to have access to full website.
                </p>
                <div className='flex flex-col'>
                    <button><i class="fa-brands fa-github"></i><span>GitHub</span></button>
                    <button><i class="fa-regular fa-paper-plane"></i><span>Go to Website</span></button>
                </div>
            </div>
            <img src={'../assets/project-1.jpeg'} alt='eCommerce' />
        </motion.div>
    )
}

export default Project1
